// Queue up list of follow-up questions
next25_btn.onclick = ()=>{
    document.getElementsByClassName("dem13")[0].style.transform = "translate(-250%, -50%)";
    dem13.classList.remove("activeInfo");

    // update global booleans
    to_check = ["past_yes", "curr_yes", "fam_yes", "dietary_yes", "med_yes"];
    n = to_check.length;
    for (let i = 0; i < n; i++){
        if(document.getElementById(to_check[i]).checked) {
          followups[i] = true;
        }
    }
    var next = null;
    for (let i = 0; i < n; i++){
        if (followups[i]){
            followups[i] = false;
            next = followups_obj[i];
            break;
        }
    }
    if (next != null){
        next.classList.add("activeInfo");
    } else{
        pics0.classList.add("activeInfo");
    }
}


